        try:
            n = int(input("Enter the length of the Fibonacci sequence you want: "))
            if n >= 0:
                return n
            else:
                print("Please enter a non-negative integer.")
        except ValueError:
            print("Please enter a valid integer.")

# Main function to run the program
def main():
    n = get_user_input()  # Get the desired sequence length from the user
    sequence = fibonacci(n)  # Generate the Fibonacci sequence
    print(f"The first {n} numbers of the Fibonacci sequence are: {sequence}")

# Run the main function if the script is executed directly
if __name__ == "__main__":
    main()
