        if (isPalindrome(userInput)) {
            document.getElementById('displayArea').innerText = "Your input is a palindrome!";
        } else {
            // Display the input as is if it's not a palindrome
            document.getElementById('displayArea').innerText = userInput;
        }
    }

    function isPalindrome(str) {
        // Remove non-alphanumeric characters and convert to lowercase
        var cleanedStr = str.replace(/[^A-Za-z0-9]/g, '').toLowerCase();

        // Compare the cleaned string to its reverse
        return cleanedStr === cleanedStr.split('').reverse().join('');
    }
